import React, { useState } from "react";
import "./homepage.css";

const FAQ = () => {
  const faqData = [
    {
      question: "What is AdSpecta?",
      answer:
        "AdSpecta is an innovative media and advertising platform. The goal of AdSpecta is to bring accessibility, transparency, and efficiency to the ad buying process.",
    },
    {
      question: "How do you book an ad on AdSpecta?",
      answer:
        "Planning an advertising strategy can be a complex process. Selecting the right media options, choosing optimal time slots, and securing cost-effective advertising rates.",
    },
    {
      question: "Outdoor Advertising Agency",
      answer:
        "AdSpecta helps brands maximize visibility with strategic outdoor advertising solutions. From billboards to transit ads, our platform offers a data-driven approach.",
    },
  ];

  const [openIndex, setOpenIndex] = useState(null);

  const toggleAnswer = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <div className="white-section"></div>

  );
};

export default FAQ;
