import React from "react";
import { FaSearch } from "react-icons/fa";
import { Card, CardContent } from "./components/ui/card";
import { Button } from "./components/ui/button";
import "./App.css";
import { useState } from 'react';
import menuIcon from "./assets/images/menuIcon.png";
import searchIcon from "./assets/images/search.png";
import loginIcon from "./assets/images/login.png";
import cartIcon from "./assets/images/cart.png";

const Header = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <header>
      <div className="hamburger-menu">
          <img src={menuIcon} alt="Menu" width="25" height="25" />
      <h1>AdSpecta</h1>
      </div>
      <div className="search">
        <input
          type="text"
          placeholder="Search Channel, Media"
        />
        <img src={searchIcon} alt="Search Icon" />
      </div>
      <div>
        <a href="#">Contact Us</a>
        <div>
          <img src={loginIcon} alt="Login" className="icon" />
          <a href="#">Login</a>
        </div>
        <div>
          <img src={cartIcon} alt="Cart" className="icon" />
          <a href="#" >Your Bag</a>
        </div>
      </div>
    </header>
  );
};

const HeroSection = () => (
  <section className="hero">
    <h2>Empowering All To Advertise</h2>
    <p>Buy best media Ad</p>
    <div className="hero-cards">
      <div className="card">Find<br />Media Rates</div>
      <div className="card">Plan<br />Media Spend</div>
      <div className="card">Buy<br />Media Spots</div>
    </div>
  </section>
);

const AdBanner = () => (
  <div className="ad-banner">
    <p className="ad-text">ðŸŽ‰ Advertising in IPL 2025 on Jio Cinema</p>
    <p>Packages Start at 5 Lakhs</p>
  </div>
);

const App = () => {
  return (
    <div>
      <Header />
      <HeroSection />
      <AdBanner />
    </div>
  );
};

export default App;
