import React from "react";
import { FaSearch } from "react-icons/fa";
import { Card, CardContent } from "./components/ui/card";
import { Button } from "./components/ui/button";
import "./App.css";
import { useState } from 'react';
import menuIcon from "assets/images/menuIcon.png";
// import searchIcon from "../assets/images/search.png";
// import loginIcon from "../assets/images/login.png";
// import cartIcon from "../assets/images/cart.png";

const Header = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <header className="flex justify-between items-center p-4 shadow-md bg-gradient-to-r from-purple-600 to-blue-500 text-white">
      <div className="flex items-center">
        <button onClick={toggleSidebar} className="mr-4">
          <img src={menuIcon} alt="Menu" width="25" height="25" />
        </button>
        <h1 className="text-lg font-bold">AdSpecta</h1>
      </div>
      <div className="flex items-center border rounded-lg px-2 py-1 bg-white text-black">
        <input
          type="text"
          placeholder="Search Channel, Media"
          className="w-96 h-10 px-3 rounded-lg border-none outline-none"
        />
        <img src="images/search.png" alt="Search Icon" className="w-6 h-6 ml-2" />
      </div>
      <div className="flex items-center space-x-6">
        <a href="#" className="text-white text-lg">Contact Us</a>
        <div className="flex items-center space-x-2">
          <img src="images/login.png" alt="Login" className="w-7 h-6" />
          <a href="#" className="text-white text-lg">Login</a>
        </div>
        <div className="flex items-center space-x-2">
          <img src="images/cart.png" alt="Cart" className="w-7 h-6" />
          <a href="#" className="text-white text-lg">Your Bag</a>
        </div>
      </div>
    </header>
  );
};

const HeroSection = () => (
  <section className="hero">
    <h2>Empowering All To Advertise</h2>
    <p>Buy best media Ad</p>
    <div className="hero-cards">
      <div className="card">Find<br />Media Rates</div>
      <div className="card">Plan<br />Media Spend</div>
      <div className="card">Buy<br />Media Spots</div>
    </div>
  </section>
);

const AdBanner = () => (
  <div className="ad-banner">
    <p className="ad-text">ðŸŽ‰ Advertising in IPL 2025 on Jio Cinema</p>
    <p>Packages Start at 5 Lakhs</p>
  </div>
);

const App = () => {
  return (
    <div>
      <Header />
      <HeroSection />
      <AdBanner />
    </div>
  );
};

export default App;
